package com.interest.mm.borrowinginterest;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;/** * Created by Bobozai09 on 2018/6/15. * 时间转换器 */public class LoanUtil {    /*         * 将时间转换为时间戳         */    public static Date dateToStamp(String s) throws ParseException {        String res;        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        Date date = simpleDateFormat.parse(s);        long ts = date.getTime();        res = String.valueOf(ts);        return date;    }    /*     * 将年月日     */    public static Date YMDToStamp(int year,int monthOfYear,int dayOfMonth) throws ParseException{        StringBuilder timerBuilder = new StringBuilder();        timerBuilder.append(year).append("-").append((monthOfYear + 1) < 10 ? "0" + (monthOfYear + 1) : monthOfYear + 1).append("-").append(dayOfMonth < 10 ? "0" + dayOfMonth : dayOfMonth).append(" ").append("00:00:00");        return dateToStamp(timerBuilder.toString());    }    /**     * 通过时间秒毫秒数判断两个时间的间隔     * @param date1     * @param date2     * @return     */    public static int differentDaysByMillisecond(Date date1,Date date2)    {        int days = (int) ((date2.getTime() - date1.getTime()) / (1000*3600*24));        return days;    }    /**     * 根据日期取当前银行利率     */    public static float getInterestRateForData (int loanType,Date date)throws ParseException{        long time=date.getTime();        float interest=0f;        for (int i=BankBaseInterest.rateTime.length-1;i>=0;i--){            interest=BankBaseInterest.interestValue[i][loanType];            if (time>=ymdToData(BankBaseInterest.rateTime[i]).getTime())                break;        }        return interest;    }    /**     * yyyy.MM.dd转Data     */    public static Date ymdToData(String ymd) throws ParseException{        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy.MM.dd HH:mm:ss");        Date date = simpleDateFormat.parse(ymd+" 00:00:00");        return date;    }}